from django.utils.http import http_date
import time
import logging

logger = logging.getLogger(__name__)

def favicon(request):
    """Respond with the favicon image."""
    response = HttpResponse(NEW_ICO, content_type='image/x-icon')
    response['Expires'] = http_date(time.time() + 86400)
    return response

def homePageView(request):

    c = Counter.objects.get(id=1)
    c.value = c.value+1
    c.save()
    logger.info('Visitor Number' + c.value + 'visited the page')

    context = {
        'Counter': c.value
    }
    return render(request, 'counter.html', context)